mapper.insert:

INSERT INTO workspace (
	deleted,workspace_name, code
)
VALUES (
	'0', #{ workspaceName }, #{ code }
)

mapper.update:
UPDATE workspace
SET workspace_name = #{ workspaceName }, code = #{ code }
WHERE workspace_no = #{ workspaceNo } and workspace_name = '0'

mapper.select:
SELECT * FROM workspace_member
WHERE workspace_no = #{ workspaceNo } 
AND ( type_no = 1 OR type_no = 2 );

SELECT COUNT(*)
FROM workspace_member
WHERE workspace_no = #{ workspaceNo }

SELECT * FROM workspace
WHERE workspace_no = #{ workspaceNo } 
LIMIT 1;

mapper.update:
UPDATE workspace
SET workspace_name = #{ workspaceName } 
WHERE workspace_no = #{ workspaceNo }

UPDATE workspace
SET deleted = '1'
WHERE workspace_no = #{ workspaceNo }

mapper.select:
SELECT workspace_no workspaceNo, email, type_no typeNo
FROM workspace_member
WHERE email = #{ email } 
LIMIT 1;

mapper.update:
UPDATE workspace_member
SET type_no = '2'
WHERE workspace_no = #{ workspaceNo } 
AND email = #{ email }

UPDATE workspace_member
SET type_no = '1'
WHERE workspace_no = #{ workspaceNo } 
AND email = #{ email }

mapper.delete:
DELETE FROM workspace_member
WHERE workspace_no = #{ workspaceNo } 
AND email = #{ email }

mapper.update:
UPDATE workspace_member
SET type_no = #{ typeNo } 
WHERE email = #{ email } AND workspace_no = #{ workspaceNo }
